import React, { Component } from 'react'
import Newsitem from './Newsitem'
import Spinner from './Spinner';

export default class News extends Component {

    constructor(){
        super();
        console.log("a constructor");
        this.state = {
        articles: [],
        loading: false,
        page:1
        }
    }
    async componentDidMount(){
        let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=d286f6ed01334582a46cc8a9e7121b05&page=1&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles, totalResults: parsedData.totalResults});
    }

    handlePrevClick = async()=>{
        let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=d286f6ed01334582a46cc8a9e7121b05&page=${this.state.page -1}&pageSize=${this.props.pageSize}`;
        this.setState({loading:true})
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            page: this.state.page - 1,
            articles: parsedData.articles
        })
    }
    handleNextClick = async ()=>{
        if(this.state.page + 1> Math.ceil(this.state.totalResults/this.props.pageSize)){
        let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=d286f6ed01334582a46cc8a9e7121b05&page=${this.state.page +1}&pageSize=${this.props.pageSize}`;
        this.setState({loading:true})
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            page: this.state.page + 1,
            articles: parsedData.articles,
            loading:false
        })
    }
    }

    render() {
        return (
            <div className='container my-3'>
                <h1>NewsMan - Top Headlines</h1>
                {this.state.loading && <Spinner/>}
                <div className="row">
                {this.state.articles.map((element)=>{
                return (<div className="col-md-4" key={element.url}>
                <Newsitem title={element.title?element.title.slice(0,40):""} description={element.description?element.description.slice(0,80):""} imageURL={element.urlToImage} newsURL={element.url}/>
                </div>)})}
                </div>
                <div className="container d-flex justify-content-evenly">
                <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Prev</button>
                <button disabled={this.state.page + 1> Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr; </button>
                </div>

            </div>
        )
    }
}
