        let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=d286f6ed01334582a46cc8a9e7121b05"
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({articles: parsedData.articles});
    }

    render() {
        return (
            <div className='container my-3'>
                <h2>NewsMan - Top Headlines</h2>
                <div className="row">
                {this.state.articles.map((element)=>{
                return (<div className="col-md-4" key={element.url}>
                <Newsitem title={element.title?element.title.slice(0,40):""} description={element.description.slice(0,80)} imageURL={element.urlToImage} newsURL={element.url}/>
